$gray_background: rgb(60, 67, 75);
$gray_letters: rgb(123, 127, 131);
$teal_button: rgb(32, 145, 163);
$book_brown: rgba(77, 63, 63, 0.63);

.body__addbook {
  font-size: 14;
  font-weight: normal;
  font-family: Arial, Helvetica, sans-serif;
  min-height: 100vh;
  width: 100vw;
  border: 2px solid rgba(0, 0, 0, 0);
  margin: 0 0 0 -3px;
  padding: 0;
  position: relative;
  overflow-x: hidden;
}

//------------------------------------------------------------------
//------------------------------------------------------------------
//MAIN

.addbook {
  margin-top: 5em;
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;
  &__title {
    margin-left: auto;
    margin-right: auto;
    font-weight: lighter;
    font-family: 'Times New Roman', Times, serif;
    color: $gray_background;
  }

  &__form {
    display: grid;
    grid-template-columns: auto auto;
    height: 55vh;

    .form__left {
      width: 70vw;
      display: inline-flex;
      height: auto;
      flex-wrap: wrap;
      padding-top: 15px;
      font-family: 'Times New Roman', Times, serif;
      font-size: 14pt;
      color: $gray_background;
      justify-content: center;
      text-align: center;
      .form__wrappers {
        width: 100%;
        height: auto;
        .form__labels {
          margin-left: 8%;
        }
        .form__input {
          width: 45vw;
          float: right;
          margin-right: 10%;
          border-radius: 6px;
          border: 1px solid $gray_letters;
          height: 70%;
        }
        .form__synopsis {
          height: 9em;
          width: 45vw;
          resize: none;
        }

        .form__published {
          height: 100%;
          width: 50%;
          margin-left: 9%;
          text-indent: 10%;
          font-size: 10pt;
          margin-right: -20px;
          float: right;
        }
        .form__published:hover {
          cursor: pointer;
        }
        .form__pages {
          height: 100%;
          width: 50%;
          float: right;
          text-indent: 10%;
          font-size: 12pt;
        }
        .form__rating {
          width: 100%;
        }
        .label__rating {
          margin-top: 1%;
          width: 100%;
        }
        .form__rating {
          margin-top: 2%;
          width: 70%;
          display: flex;
          justify-content: space-evenly;
          .star__rating {
            font-size: 19pt;
            cursor: pointer;
          }
        }
      }

      .input__smaller {
        height: 10%;
        width: 80%;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
      }
      .wrappers__sidebyside {
        height: 100%;
        width: 45%;
        display: block;
        flex-wrap: nowrap;
        justify-content: space-between;
      }
      .published__wrapper {
        margin-left: -8%;
        width: 47%;
      }
      .pages__wrapper {
        margin-right: -4%;
        width: 50%;
      }
      .rating__wrapper {
        height: 10%;
        width: 75%;
        display: inline-flex;
        margin-top: -3%;
        margin-left: -15%;
        align-content: center;
      }
      .rating__radio {
        display: none;
      }
      .form__rating {
        width: 100%;
        display: grid;
        grid-template-columns: auto auto auto auto auto;
        float: right;
      }
      .fa-star {
        margin-left: 5px;
        font-size: 20pt;
      }
    }
    .form__right {
      width: 30vw;
      align-content: center;
      justify-content: center;

      .image__frame {
        height: 75%;
        width: 80%;
        background-color: rgb(228, 228, 228);
        display: flex;
        flex-wrap: wrap;
        align-content: center;
        justify-content: center;
      }
      .image__upload {
        margin-top: 3em;
        height: 8%;
        width: 80%;
        border: none;
        border-radius: 5px;
        color: white;
        background-color: $gray_letters;
        font-weight: bold;
        font-size: 12pt;
      }
      .image__upload:hover {
        cursor: pointer;
      }
    }
  }
  &__btnwrap {
    width: 100%;
    height: fit-content;
    display: flex;
    justify-content: center;
    position: absolute;
    margin: 67vh 0 0 0;
  }
  &__button {
    height: 3em;
    background-color: rgb(230, 230, 230);
    width: 30%;
    color: $gray_background;
    font-size: 11pt;
    border-radius: 7px;
    margin: -35px 20px 0 20px;
    font-family: Arial, Helvetica, sans-serif;
    font-weight: bold;
    border: none;
  }
  &__button:hover {
    cursor: pointer;
  }
  .button--dark {
    background-color: $gray_background;
    color: white;
  }
  .NavCancel {
    text-decoration: none;
    color: $gray_background;
  }
}

