.edit__page {
  width: 100%;
}
//------------------------------------------------------------------
//------------------------------------------------------------------
//MAIN

.edit__main {
  margin-top: 5em;
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;

  .edit__title {
    margin-left: auto;
    margin-right: auto;
    font-weight: lighter;
