    color: $gray_background;
  }

  .edit__form {
    display: grid;
    grid-template-columns: auto auto;
    height: 55vh;

    .form__left {
      width: 70vw;
      display: inline-flex;
      height: auto;
      flex-wrap: wrap;
      padding-top: 15px;
      font-family: 'Times New Roman', Times, serif;
      font-size: 14pt;
      color: $gray_background;
      justify-content: center;
      text-align: center;
      .form__wrappers {
        width: 100%;
        height: auto;
        .form__labels {
          margin-left: 8%;
        }
        .form__input {
          width: 45vw;
          float: right;
          margin-right: 10%;
          border-radius: 6px;
          border: 1px solid $gray_letters;
          height: 70%;
        }
        .form__synopsis {
          height: 9em;
          width: 45vw;
          resize: none;
          font-family: Arial, Helvetica, sans-serif;
          line-height: 15pt;
        }

        .form__published {
          height: 100%;
          width: 50%;
          margin-left: 9%;
          text-indent: 10%;
          font-size: 10pt;
          margin-right: -20px;
          float: right;
        }
        .form__pages {
          height: 100%;
          width: 50%;
          float: right;
          text-indent: 10%;
          font-size: 12pt;
        }
        .form__rating {
          width: 100%;
        }
        .label__rating {
          margin-top: 1%;
          width: 100%;
        }
        .form__rating {
          margin-top: 2%;
          width: 70%;
          display: flex;
          justify-content: space-evenly;
          .star__rating {
            font-size: 19pt;
            cursor: pointer;
          }
        }
      }

      .input__smaller {
        height: 10%;
        width: 80%;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
      }
      .wrappers__sidebyside {
        height: 100%;
        width: 45%;
        display: block;
        flex-wrap: nowrap;
        justify-content: space-between;
      }
      .published__wrapper {
        margin-left: -8%;
        width: 47%;
      }
      .pages__wrapper {
        margin-right: -4%;
        width: 50%;
      }
      .rating__wrapper {
        height: 10%;
        width: 75%;
        display: inline-flex;
        margin-top: -3%;
        margin-left: -15%;
        align-content: center;
      }
      .rating__radio {
        display: none;
      }
      .form__rating {
        width: 100%;
        display: grid;
        grid-template-columns: auto auto auto auto auto;
        float: right;
      }
      .fa-star {
        margin-left: 5px;
        font-size: 20pt;
      }
    }
    .form__right {
      width: 30vw;
      align-content: center;
      justify-content: center;

      .image__frame {
        height: 70%;
        max-height: 550px;
        width: 80%;
        display: flex;
        flex-wrap: wrap;
        align-content: center;
        justify-content: center;

        .book__cover {
          height: auto;
          width: 100%;
          max-width: 300px;
          max-height: 400px;
        }
      }
      .image__upload {
        margin-left: -2%;
        margin-top: 2em;
        height: 8%;
        width: 80%;
        border: none;
        border-radius: 5px;
        color: white;
        background-color: $gray_letters;
        font-weight: bold;
        font-size: 12pt;
      }
    }
  }
  .edit__btnwrap {
    width: 100%;
    height: fit-content;
    display: flex;
    justify-content: center;
    align-content: center;
    position: absolute;
    margin: 66vh 0 0 0;
  }
  .edit__button {
    height: 3em;
    background-color: rgb(230, 230, 230);
    width: 30%;
    color: $gray_background;
    font-size: 11pt;
    border-radius: 7px;
    margin: -35px 20px 0 20px;
    font-family: Arial, Helvetica, sans-serif;
    font-weight: bold;
    border: none;
  }
  .button--dark {
    background-color: $gray_background;
    color: white;
  }
  .cancel__button--nav {
    margin-top: -35px;
    width: 30%;
    height: 3em;
  }
  .cancel__button {
    width: 100%;
    height: 3em;
    margin: 0;
  }
  .edit__button:hover {
    color: gray;
  }
}

