    height: auto;
    overflow-y: scroll;
    .main__detailcard {
      flex-direction: row;
      flex-wrap: wrap;
      padding: 0;
      width: 90%;
    }
    .main__coverwrap {
      width: 100vw;
      height: fit-content;
      margin-left: auto;
      margin-right: auto;
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
      text-align: center;

      .book__title--mobile {
        display: flex;
        width: fit-content;
        font-weight: lighter;
        margin-right: auto;
        margin-left: auto;
      }
      .book__cover {
        height: auto;
        width: 60%;
        min-height: 0px;
        max-height: 50vh;
        max-width: 50vw;
        min-width: 0px;
        margin-left: 22.5%;
      }
      .author__mobile {
        display: flex;
        width: fit-content;
        font-weight: lighter;
        margin-right: auto;
        margin-left: auto;
      }
      .book__stars {
        background-color: white;
        max-width: 85%;
        min-width: 0px;
        width: 95%;
        margin: 1% auto 0 auto;
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        height: fit-content;
        text-align: center;

        .book__rating {
          display: flex;
          margin-left: auto;
          margin-right: auto;
          text-align: center;
          width: fit-content;
          margin-top: 0;
          margin-bottom: 15px;
          font-weight: lighter;
        }
        .star__wrapper {
          width: 100%;
          display: inline-flex;
        }
        .fa-star {
          margin-left: auto;
          margin-right: auto;
          margin-top: auto;
          margin-bottom: auto;
          display: block;
          width: 20%;
        }
        .star__rating {
          font-size: 20pt;
          height: auto;
          width: 1em;
        }
      }
    }
    .book__details {
      font-size: 12pt;
      width: 100%;
      margin-top: 2%;
      text-align: center;
      padding: 10px;
      margin-left: auto;
      margin-right: auto;
      height: auto;
      .book__title {
        display: none;
      }
      .book__author {
        display: none;
      }
    }
    .main__btnwrap {
      margin-top: 20%;
      margin-bottom: 50px;
      display: flex;
      flex-wrap: wrap;
      flex-direction: row;
      position: relative;
      width: 100%;
      margin-left: auto;
      margin-right: auto;
    }
    .shelf__link {
      width: 85vw;
      margin: 0;
      margin-top: 15px;
      display: flex;
      justify-content: center;
    }
    .edit__link {
      width: 85vw;
      margin: 0;
      display: flex;
      justify-content: center;
    }
    .main__button {
      width: 75%;
      margin-bottom: 50px;
    }
    .button--dark {
    }
    .delete__button {
      margin-left: -20%;
    }
  }
} //media query mobile end bracket
