$gray_background: rgb(60, 67, 75);
$gray_letters: rgb(123, 127, 131);
$teal_button: rgb(32, 145, 163);
$book_brown: rgba(77, 63, 63, 0.63);

.page__bookshelf {
  margin-top: 0;
  display: flex;
  justify-content: center;
  font-size: 14;
  font-weight: normal;
  font-family: Arial, Helvetica, sans-serif;
  min-height: 100vh;
  width: 100vw;
  border: 2px solid rgba(0, 0, 0, 0);
  margin: 0 0 0 -3px;
  padding: 0;
  position: relative;
  overflow-y: hidden;
}

.main__bookshelf {
  margin-top: 4.8em;
  width: 100%;
  height: 85vh;
  text-align: center;
  overflow: hidden;

  .clear__button {
    position: absolute;
    left: 5%;
    top: 10%;
    background-color: $gray_background;
    color: white;
    font-weight: bold;
    padding: 9px;
    border-radius: 5px;
  }
  .main__searchwrapper {
    display: none;
  }
  .main__title {
    font-weight: lighter;
    font-style: italic;
    font-family: 'Times New Roman', Times, serif;
    color: $gray_background;
    margin-bottom: 2%;
  }
  .main__gridcontainer {
    margin-left: auto;
    margin-right: auto;
    height: 85%;
    width: 82vw;
    display: grid;
    overflow-y: scroll;
    grid-template-columns: auto auto auto;
    grid-template-rows: auto auto auto;
    justify-content: space-evenly;
    align-content: space-between;
    margin-top: 5px;

    .grid__items {
      justify-items: center;
