      border: 1px solid $gray_letters;
      margin-bottom: 30px;
      font-size: 12pt;
      font-family: 'Times New Roman', Times, serif;
      color: $gray_background;
      .book__link {
        text-decoration: none;
        color: black;
      }
      .grid__bookcover {
        margin-top: 8px;
        height: 130px;
        width: auto;
      }
      .grid__title {
        margin-left: 5px;
        margin-right: 5px;
      }
      .grid__author {
        font-size: 10pt;
      }
    }
  }
}

//Scroll bar customs
.main__gridcontainer::-webkit-scrollbar {
