}

.main__gridcontainer::-webkit-scrollbar-track {
  border-radius: 5px;
  border-left: 3px solid transparent;
  border-right: 3px solid transparent;
  -webkit-box-shadow: inset 0 0 10px rgba(185, 185, 185, 0.3);
}

.main__gridcontainer::-webkit-scrollbar-thumb {
  border-radius: 5px;
  border: 10px solid $gray_background;
  width: 15px;
  background-color: rgb(121, 117, 117);
  outline: 0 solid slategrey;
}

